import React, { useState } from "react";
import { isEmailValid, isNameValid } from "../../helpers/validate";

const Registration = () => {
  const [name, setName] = useState("");
  const [nameErr, setNameErr] = useState("");
  const [email, setEmail] = useState("");
  const [emailErr, setEmailErr] = useState("");
  const [mobile, setMobile] = useState("");
  const [mobileErr, setMobileErr] = useState("");

  const customCentered = {
    alignItems: "center",
    minHeight: "100vh",
    margin: "0 auto",
    maxWidth: "50%",
  };
  const checkName = async () => {
    const nameValid = isNameValid(name);
    if (!nameValid) {
      setNameErr("Please enter a valid name");
    } else {
      setNameErr("");
    }
  };
  const checkEmail = async () => {
    const emailValid = isEmailValid(email);
    if (!emailValid) {
      setEmailErr("Please enter a valid email");
    } else {
      setEmailErr("");
    }
  };
  const onSubmit = (data) => {
    console.log(data);
  };
  return (
    <>
      <div className="container ">
        <div className=" row d-flex" style={customCentered}>
          <div className="col-auto">
            <h3>Registration Form</h3>
            <form className="" onSubmit={onSubmit}>
              <div className="row mb-3">
                <div className=" form-group g-2">
                  <input
                    type="text"
                    className={`form-control ${nameErr ? "is-invalid" : ""}`}
                    placeholder="Enter your Name"
                    aria-label="Name"
                    name="name"
                    onBlur={checkName}
                    autoComplete="off"
                    handleChange={(e) => setName(e.target.value)}
                  />
                  {nameErr ? (
                    <span className="input-error-message" key={nameErr}>
                      {nameErr}
                    </span>
                  ) : null}
                </div>
                <div className=" form-group g-2">
                  <input
                    type="text"
                    className={`form-control ${emailErr ? "is-invalid" : ""}`}
                    placeholder="Enter your Email"
                    aria-label="Email"
                    name="email"
                    autoComplete="off"
                    handleChange={(e) => setEmail(e.target.value)}
                    onBlur={checkEmail}
                  />
                  {emailErr ? (
                    <span className="input-error-message" key={emailErr}>
                      {emailErr}
                    </span>
                  ) : null}
                </div>

                <div className="form-group g-2">
                  <input
                    type="text"
                    className={`form-control ${
                      errors.mobile ? "is-invalid" : ""
                    }`}
                    placeholder="Enter your Mobile Number"
                    aria-label="mobile"
                    name="mobile"
                    autoComplete="off"
                    {...register("mobile", {
                      required: false,
                      minLegth: {
                        value: 10,
                        message: "please enter valid number",
                      },
                      pattern: {
                        value: /^\d+$/,
                        message: "error message",
                      },
                    })}
                  />
                  {errors.mobile && errors.mobile.message}
                </div>

                <div className="form-group g-2">
                  <button type="submit" className="btn btn-primary">
                    Submit
                  </button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
};
export default Registration;
